// src/main.js
import Vue from 'vue';
import App from './App.vue';
import router from './router'; // Importamos las rutas que hemos definido
import axios from 'axios'; // Para manejar peticiones HTTP

Vue.config.productionTip = false;

// Configuraci贸n global de axios (opcional)
axios.defaults.baseURL = 'http://localhost:5000/api'; // Backend corriendo en el puerto 5000
Vue.prototype.$http = axios; // Hacer axios disponible en toda la aplicaci贸n

new Vue({
  router, // Incluimos el router
  render: h => h(App),
}).$mount('#app');
